//Alerta eliminar en actualizar
function SwalDeleteActualizarFormacion(id) {

  Swal.fire({
    title: 'Alerta',
    text: "Â¿EstÃ¡s seguro de eliminar esta formaciÃ³n?",
    icon: 'warning',
    iconColor: 'red',
    showCancelButton: true,
    confirmButtonColor: '#5BB318',
    cancelButtonColor: '#d33',
    confirmButtonText: 'Si, eliminar',
    cancelButtonText: 'No, cancelar',
    reverseButtons: true
  }).then((result) => {
    if (result.isConfirmed) {

      MandaridFormacion(id)
      eliminarFila(id)

      function eliminarFila(ID) {
        //document.querySelector(".borrarFormacion").deleteRow(id);
        const valor = document.getElementById("1" + ID.id)
        console.log(valor)
        function eliminarFila() {
          valor.parentNode.removeChild(valor);
        }
        eliminarFila()
      }

      sleep(900);

      Swal.fire(
        'Eliminado!',
        'Registro eliminado correctamente',
        'success'
      )

    } else if (
      result.dismiss === Swal.DismissReason.cancel
    ) {
      Swal.fire(
        'Cancelado',
        'Tu registro estÃ¡ a salvo',
        'error'
      )
    }
  })
}


